import React, { useEffect, useState } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";
import { Card, Button, Container, Row } from "react-bootstrap";
import RecipeCard from "../RecipeCard";

const RecipeList = () => {
	const [recipes, setRecipes] = useState([]);
	const { query } = useParams();
	const [page, setPage] = useState(0);
	const [currentIndex, setCurrentIndex] = useState(0);
	const appId = process.env.REACT_APP_EDAMAM_APP_ID;
	const appKey = process.env.REACT_APP_EDAMAM_APP_KEY;

	const fetchRecipes = () => {
		axios
			.get(
				`https://api.edamam.com/search?q=${query}&app_id=${appId}&app_key=${appKey}&from=${page}&to=${
					page + 10
				}`
			)
			.then((response) => {
				setRecipes((prevRecipes) => [...prevRecipes, ...response.data.hits]);
				setPage((prevPage) => prevPage + 10);
			})
			.catch((error) => console.error("Error:", error));
	};

	useEffect(() => {
		fetchRecipes();
	}, []);

	const handleNext = () => {
		if (currentIndex === recipes.length - 1) {
			fetchRecipes();
		}
		setCurrentIndex(currentIndex + 1);
	};

	const handlePrev = () => {
		if (currentIndex > 0) {
			setCurrentIndex(currentIndex - 1);
		}
	};

	const recipe = recipes[currentIndex];

	if (!recipe) return <p>Loading...</p>;

	return (
		<Container className='d-flex justify-content-center align-items-center'>
			<Button
				onClick={handlePrev}
				disabled={currentIndex === 0}>
				Previous
			</Button>
			<RecipeCard recipe={recipe.recipe} />
			<Button
				onClick={handleNext}
				disabled={currentIndex === recipes.length - 1}>
				Next
			</Button>
		</Container>
	);
};

export default RecipeList;
