import React from "react";
import { Card, Button } from "react-bootstrap";
import NutritionInfo from "./NutritionInfo";
import ExpandableList from "./ExpandableList";

const RecipeCard = ({ recipe, isExpanded, setIsExpanded }) => {
	return (
		<Card
			className='mx-2 shadow p-3 mb-5 bg-white rounded mx-5 my-5'
			style={{ backgroundColor: "#f8f9fa", width: "40rem" }}>
			<Button onClick={() => handleStarClick(recipe)}>Star</Button>
			<Card.Img
				variant='top'
				src={recipe.image}
				style={{ maxHeight: "250px", objectFit: "scale-down" }}
			/>
			<Card.Body className='d-flex flex-column'>
				<Card.Title className='border-bottom text-primary'>
					{recipe.label}
				</Card.Title>
				<Card.Text className=''>
					<ExpandableList
						items={recipe.ingredientLines}
						isExpanded={isExpanded}
						setIsExpanded={setIsExpanded}
					/>
					<div className='mt-4 bg-light p-3 rounded '>
						<h4 className='mb-3'>Nutrition Facts</h4>
						<NutritionInfo recipe={recipe} />
					</div>
				</Card.Text>
			</Card.Body>
		</Card>
	);
};

export default RecipeCard;
