import React, { useEffect, useState } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";

const RecipeDetail = () => {
	const [recipe, setRecipe] = useState(null);
	const { id } = useParams(); // id här kommer att vara URI:n för receptet.
	const appId = process.env.REACT_APP_EDAMAM_APP_ID_DATABASE;
	const appKey = process.env.EDAMAM_APP_KEY_DATABASE;

	useEffect(() => {
		axios
			.get(
				`https://api.edamam.com/search?r=${id}&app_id=${appId}&app_key=${appKey}`
			)
			.then((response) => {
				// API:et returnerar en array med ett enda objekt som är receptdetaljerna.
				if (response.data && response.data.length > 0) {
					setRecipe(response.data[0]);
				}
			})
			.catch((error) => console.error("Error:", error));
	}, [id]);

	if (!recipe) {
		return <div>Loading...</div>;
	}

	return (
		<div>
			<h1>{recipe.label}</h1>
			<img
				src={recipe.image}
				alt={recipe.label}
			/>
			<ul>
				{recipe.ingredientLines.map((ingredient, index) => (
					<li key={index}>{ingredient}</li>
				))}
			</ul>
		</div>
	);
};

export default RecipeDetail;
