import React, { useEffect, useState } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";

const RecipeList = () => {
	const [recipes, setRecipes] = useState([]);
	const { query } = useParams();
	const [servingSize, setServingSize] = useState(4);

	useEffect(() => {
		const appId = process.env.REACT_APP_EDAMAM_APP_ID;
		const appKey = process.env.REACT_APP_EDAMAM_APP_KEY;
		axios
			.get(
				`https://api.edamam.com/search?q=${query}&app_id=${appId}&app_key=${appKey}`
			)
			.then(
				(response) =>
					console.log(response.data.hits) || setRecipes(response.data.hits)
			)
			.catch((error) => console.error("Error:", error));
	}, [query]);

	const handleServingChange = (event) => {
		setServingSize(event.target.value);
	};

	return (
		<div>
			{recipes.map((recipe, index) => (
				<div key={index}>
					<h2>{recipe.recipe.label}</h2>
					<img
						src={recipe.recipe.image}
						alt={recipe.recipe.label}
					/>
					<p>{recipe.recipe.ingredientLines}</p>
					<p>
						<label>
							Serving size:
							<input
								type='number'
								value={servingSize}
								onChange={handleServingChange}
							/>
						</label>
					</p>
					{/* 
					<div>
						<p>
							Energy:{" "}
							{parseFloat(
								(
									(recipe.recipe.totalNutrients?.ENERC_KCAL?.quantity /
										recipe.recipe.yield) *
									servingSize
								).toFixed(2)
							)}{" "}
							{recipe.recipe.totalNutrients?.ENERC_KCAL?.unit}
						</p>
						<p>
							Fat:{" "}
							{parseFloat(
								(
									(recipe.recipe.totalNutrients?.FAT?.quantity /
										recipe.recipe.yield) *
									servingSize
								).toFixed(2)
							)}{" "}
							{recipe.recipe.totalNutrients?.FAT?.unit}
						</p>
						<p>
							Carbs:{" "}
							{parseFloat(
								(
									(recipe.recipe.totalNutrients?.CHOCDF?.quantity /
										recipe.recipe.yield) *
									servingSize
								).toFixed(2)
							)}{" "}
							{recipe.recipe.totalNutrients?.CHOCDF?.unit}
						</p>
						<p>
							Protein:{" "}
							{parseFloat(
								(
									(recipe.recipe.totalNutrients?.PROCNT?.quantity /
										recipe.recipe.yield) *
									servingSize
								).toFixed(2)
							)}{" "}
							{recipe.recipe.totalNutrients?.PROCNT?.unit}
						</p>
					</div> */}
				</div>
			))}
		</div>
	);
};

export default RecipeList;
