import React, { useEffect, useState } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";
import { Link } from "react-router-dom";

const RecipeList = () => {
	const [recipes, setRecipes] = useState([]);
	const { query } = useParams();
	const appId = process.env.REACT_APP_EDAMAM_APP_ID;
	const appKey = process.env.REACT_APP_EDAMAM_APP_KEY;

	useEffect(() => {
		axios
			.get(
				`https://api.edamam.com/search?q=${query}&app_id=${appId}&app_key=${appKey}`
			)
			.then(
				(response) =>
					console.log(response.data.hits) || setRecipes(response.data.hits)
			)
			.catch((error) => console.error("Error:", error));
	}, [query]);

	return (
		<div>
			{recipes.map((recipe, index) => (
				<div key={index}>
					<h2>{recipe.recipe.label}</h2>
					<img
						src={recipe.recipe.image}
						alt={recipe.recipe.label}
					/>
					<p>{recipe.recipe.ingredientLines}</p>
					{Object.entries(recipe.recipe.totalNutrients).map(
						([key, nutrient], index) => (
							<p key={index}>
								{nutrient.label}: {nutrient.quantity} {nutrient.unit}
							</p>
						)
					)}
				</div>
			))}
		</div>
	);
};

export default RecipeList;
