import React from "react";
import { Card } from "react-bootstrap";
import NutritionInfo from "./NutritionInfo";
import ExpandableList from "./ExpandableList";

const RecipeCard = ({ recipe }) => {
	return (
		<Card
			className='mx-2 shadow p-3 mb-5 bg-white rounded mx-5 my-5'
			style={{ backgroundColor: "#f8f9fa", width: "40rem" }}>
			<Card.Img
				variant='top'
				src={recipe.image}
				style={{ maxHeight: "250px", objectFit: "scale-down" }}
			/>
			<Card.Body className='d-flex flex-column justify-content-between'>
				<Card.Title className='border-bottom text-primary'>
					{recipe.label}
				</Card.Title>
				<Card.Text>
					<ExpandableList items={recipe.ingredientLines} />
					<NutritionInfo recipe={recipe} />
				</Card.Text>
			</Card.Body>
		</Card>
	);
};

export default RecipeCard;
