import React, { useEffect, useState, useRef } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";
import { Card, Row, Col, Button } from "react-bootstrap";

const RecipeList = () => {
	const [recipes, setRecipes] = useState([]);
	const { query } = useParams();
	const [loading, setLoading] = useState(false);
	const [page, setPage] = useState(0);

	const appId = process.env.REACT_APP_EDAMAM_APP_ID;
	const appKey = process.env.REACT_APP_EDAMAM_APP_KEY;

	const fetchRecipes = () => {
		setLoading(true);
		axios
			.get(
				`https://api.edamam.com/search?q=${query}&app_id=${appId}&app_key=${appKey}&from=${page}&to=${
					page + 10
				}`
			)
			.then((response) => {
				setRecipes((prevRecipes) => [...prevRecipes, ...response.data.hits]);
				setPage((prevPage) => prevPage + 10);
			})
			.catch((error) => console.error("Error:", error))
			.finally(() => {
				setLoading(false);
			});
	};

	useEffect(() => {
		fetchRecipes();
	}, []);

	const scrollContainer = useRef(null);

	useEffect(() => {
		const handleScroll = () => {
			if (
				scrollContainer.current.scrollLeft +
					scrollContainer.current.clientWidth >=
				scrollContainer.current.scrollWidth
			) {
				if (!loading) {
					fetchRecipes();
				}
			}
		};

		if (scrollContainer.current) {
			scrollContainer.current.addEventListener("scroll", handleScroll);
		}

		return () => {
			if (scrollContainer.current) {
				scrollContainer.current.removeEventListener("scroll", handleScroll);
			}
		};
	}, [loading]);

	return (
		<Row
			className='gx-3 gy-3 flex-row flex-nowrap overflow-auto'
			ref={scrollContainer}>
			{recipes.map((recipe, index) => (
				<Col
					key={index}
					xs={12}
					md={4}>
					<Card className='h-100'>
						<Card.Img
							variant='top'
							src={recipe.recipe.image}
							style={{ maxHeight: "200px", objectFit: "cover" }}
						/>
						<Card.Body className='d-flex flex-column justify-content-between'>
							<Card.Title>{recipe.recipe.label}</Card.Title>
							<Card.Text>
								<ul>
									{recipe.recipe.ingredientLines.map((ingredient, i) => (
										<li key={i}>{ingredient}</li>
									))}
								</ul>
								<div className='text-center'>
									<div className='mt-3'>
										<p>
											<strong>Energy:</strong>{" "}
											{parseFloat(
												(recipe.recipe.totalNutrients?.ENERC_KCAL?.quantity /
													recipe.recipe.yield) *
													4
											).toFixed(2)}{" "}
											{recipe.recipe.totalNutrients?.ENERC_KCAL?.unit}
										</p>
										<p>
											<strong>Fat:</strong>{" "}
											{parseFloat(
												(recipe.recipe.totalNutrients?.FAT?.quantity /
													recipe.recipe.yield) *
													4
											).toFixed(2)}{" "}
											{recipe.recipe.totalNutrients?.FAT?.unit}
										</p>
										<p>
											<strong>Carbs:</strong>{" "}
											{parseFloat(
												(recipe.recipe.totalNutrients?.CHOCDF?.quantity /
													recipe.recipe.yield) *
													4
											).toFixed(2)}{" "}
											{recipe.recipe.totalNutrients?.CHOCDF?.unit}
										</p>
										<p>
											<strong>Protein:</strong>{" "}
											{parseFloat(
												(recipe.recipe.totalNutrients?.PROCNT?.quantity /
													recipe.recipe.yield) *
													4
											).toFixed(2)}{" "}
											{recipe.recipe.totalNutrients?.PROCNT?.unit}
										</p>
									</div>
								</div>
							</Card.Text>
						</Card.Body>
					</Card>
				</Col>
			))}
		</Row>
	);
};

export default RecipeList;
