import React, { useEffect, useState } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";
import { Card, Row, Col, Image, Button } from "react-bootstrap";

const RecipeList = () => {
	const [recipes, setRecipes] = useState([]);
	const { query } = useParams();
	const [servingSize, setServingSize] = useState({});
	const [showNutrition, setShowNutrition] = useState({});
	const [showMore, setShowMore] = useState({});
	const [selectedCard, setSelectedCard] = useState(null);

	const appId = process.env.REACT_APP_EDAMAM_APP_ID;
	const appKey = process.env.REACT_APP_EDAMAM_APP_KEY;

	useEffect(() => {
		axios
			.get(
				`https://api.edamam.com/search?q=${query}&app_id=${appId}&app_key=${appKey}`
			)
			.then((response) => setRecipes(response.data.hits))
			.catch((error) => console.error("Error:", error));
	}, [query]);

	const handleServingChange = (index, event) => {
		setServingSize((prevServingSize) => ({
			...prevServingSize,
			[index]: event.target.value,
		}));
	};

	const handleToggleNutrition = (index) => {
		setShowNutrition((prevShowNutrition) => ({
			...prevShowNutrition,
			[index]: !prevShowNutrition[index],
		}));
	};

	const handleToggleShowMore = (index) => {
		setShowMore((prevShowMore) => ({
			...prevShowMore,
			[index]: !prevShowMore[index],
		}));
	};

	const handleCardClick = (index) => {
		setSelectedCard(index);
	};

	return (
		<Row className='gx-3 gy-3'>
			{recipes.map((recipe, index) => {
				const showNutritionDetails = showNutrition[index] || false;
				const showMoreIngredients = showMore[index] || false;
				const currentServingSize = servingSize[index] || 4;

				return (
					<Col
						key={index}
						xs={12}
						md={4}>
						<Card
							className={`h-100 ${
								selectedCard === index ? "selected-card" : ""
							}`}
							onClick={() => handleCardClick(index)}>
							<Card.Img
								variant='top'
								src={recipe.recipe.image}
								style={{ maxHeight: "200px", objectFit: "cover" }}
							/>
							<Card.Body className='d-flex flex-column justify-content-between'>
								<Card.Title>{recipe.recipe.label}</Card.Title>
								<Card.Text>
									<ul>
										{recipe.recipe.ingredientLines
											.slice(0, showMoreIngredients ? undefined : 5)
											.map((ingredient, i) => (
												<li key={i}>{ingredient}</li>
											))}
									</ul>
									{recipe.recipe.ingredientLines.length > 5 && (
										<Button
											variant='link'
											onClick={() => handleToggleShowMore(index)}>
											{showMoreIngredients ? "Show Less" : "Show More"}
										</Button>
									)}
									<p>
										<label>
											Serving size:
											<input
												type='number'
												value={currentServingSize}
												onChange={(event) => handleServingChange(index, event)}
											/>
										</label>
									</p>
									<div className='text-center'>
										<Button
											variant='outline-primary'
											onClick={() => handleToggleNutrition(index)}>
											{showNutritionDetails
												? "Hide Nutritional Info"
												: "Show Nutritional Info"}
										</Button>
										{showNutritionDetails && (
											<div className='mt-3'>
												<p>
													<strong>Energy:</strong>{" "}
													{parseFloat(
														(
															(recipe.recipe.totalNutrients?.ENERC_KCAL
																?.quantity /
																recipe.recipe.yield) *
															currentServingSize
														).toFixed(2)
													)}{" "}
													{recipe.recipe.totalNutrients?.ENERC_KCAL?.unit}
												</p>
												<p>
													<strong>Fat:</strong>{" "}
													{parseFloat(
														(
															(recipe.recipe.totalNutrients?.FAT?.quantity /
																recipe.recipe.yield) *
															currentServingSize
														).toFixed(2)
													)}{" "}
													{recipe.recipe.totalNutrients?.FAT?.unit}
												</p>
												<p>
													<strong>Carbs:</strong>{" "}
													{parseFloat(
														(
															(recipe.recipe.totalNutrients?.CHOCDF?.quantity /
																recipe.recipe.yield) *
															currentServingSize
														).toFixed(2)
													)}{" "}
													{recipe.recipe.totalNutrients?.CHOCDF?.unit}
												</p>
												<p>
													<strong>Protein:</strong>{" "}
													{parseFloat(
														(
															(recipe.recipe.totalNutrients?.PROCNT?.quantity /
																recipe.recipe.yield) *
															currentServingSize
														).toFixed(2)
													)}{" "}
													{recipe.recipe.totalNutrients?.PROCNT?.unit}
												</p>
											</div>
										)}
									</div>
								</Card.Text>
							</Card.Body>
						</Card>
					</Col>
				);
			})}
		</Row>
	);
};

export default RecipeList;
